mod utils;

use blake2::{digest::consts::U32, Blake2b};
use hex::FromHex;
use mithril_stm::stm::{StmAggrSig, StmAggrVerificationKey, StmParameters};
use serde::de::DeserializeOwned;
use wasm_bindgen::prelude::*;

#[wasm_bindgen]
extern "C" {
    fn alert(s: &str);
}

pub(crate) type D = Blake2b<U32>;

pub fn key_decode_hex<T>(from: &str) -> T
where
    T: DeserializeOwned,
{
    let from_vec =
        Vec::from_hex(from).expect("Key decode hex: can not turn hexadecimal value into bytes");

    serde_json::from_slice(from_vec.as_slice()).unwrap_or_else(|_| {
        panic!(
            "Key decode hex: can not deserialize to type '{}' from binary JSON",
            std::any::type_name::<T>()
        )
    })
}
//{"k":5,"m":100,"phi_f":0.65}
struct Certificate {
    signature: StmAggrSig<D>,
    avk: StmAggrVerificationKey<D>,
    message: Vec<u8>,
    protocol_parameters: StmParameters,
}

impl Certificate {
    pub fn new(signature: &str, avk: &str, message: &str) -> Self {
        Self {
            signature: key_decode_hex(signature),
            avk: key_decode_hex(avk),
            message: message.as_bytes().to_owned(),
            protocol_parameters: StmParameters {
                m: 100,
                k: 5,
                phi_f: 0.65,
            },
        }
    }

    pub fn verify(&self) -> Result<(), String> {
        self.signature
            .verify(&self.message, &self.avk, &self.protocol_parameters)
            .map_err(|e| format!("ERROR: {e:?}"))
    }
}

#[wasm_bindgen]
pub fn main() {
    let signature = "";
    let avk = "";
    let message = "765e790269f21055e42d019386f9398784238a8e9b7aa5b3ad4d984f75ab756b ";
    let certificate = Certificate::new(signature, avk, message);

    if let Err(msg) = certificate.verify() {
        alert(&msg);
    } else {
        alert("TADAAAAA");
    }
}
